    #include "globals.h"

    struct fruit{
        int x_pos, y_pos;
        int color_id;
        short int fruit_data[FRUIT_SIZE];

        bool isSmashed; // Initialize to 0. Push to 1 when fruit is part of a match mesh.
    };

    // Function to initialize a fruit instance with fruit pic values
    struct fruit initialize_fruit(int x, int y, int colour_ID, short int fruit_pic[FRUIT_SIZE]) {
        struct fruit new_fruit;
        new_fruit.x_pos = x;
        new_fruit.y_pos = y;
        new_fruit.color_id = colour_ID;
        for (int i = 0; i < FRUIT_SIZE; ++i) {
            new_fruit.fruit_data[i] = fruit_pic[i];  // Initialize each element of fruit_data with values from fruit_pic
        }
        new_fruit.isSmashed = false;
        return new_fruit;
    };

